const defaultcharacter = {
  thumbnail_url: 'https://render.worldofwarcraft.com/us/character/malganis/130/235234178-avatar.jpg?alt=wow/static/images/2d/avatar/10-0.jpg',
  name:'Eremes',
  class:'Demon Hunter',
  spec: 'Havoc',
  score:'2500',
};

const roleIconMapping = {
  TANK: './images/damage.png',
  HEALING: './images/healer.png',
  DPS: './images/tank.png',
};

*/
const getRoleIcon = (role) => {
  switch (role) {
    case 'TANK':
      return 'ðŸ›¡ï¸';
    case 'HEALING':
